export default async function parseXmlSitemap(sitemapUri){sitemapUri.includes(".xml")||console.warn(`Please supply an XML file. Supplied: ${sitemapUri}`);const sitemapUrls=[];try{await fetch(sitemapUri).then(response=>response.ok?response.text():console.error(`Error fetching sitemap at: ${sitemapUri}`)).then(string=>(new window.DOMParser).parseFromString(string,"text/html")).then(data=>{const locations=[...data.querySelectorAll("loc")];0===locations.length&&console.warn(`No <loc> present in ${sitemapUri} See: https://www.sitemaps.org/protocol.html#locdef`),locations.forEach(loc=>{const url=new URL(loc.textContent);sitemapUrls.push(url)})})}catch(e){console.error(e)}return sitemapUrls}